/**
 * Provides the "ngeoModal" directive.
 *
 * This directive shows a Bootstrap modal when the `ngModel` expression
 * evaluates to `true`, and it hides it when the `ngModel` expression
 * evaluates to `false`.
 *
 * The directives also changes the `ngModel` value when the user manually
 * closes the modal.
 *
 * This directive is based on Bootstrap's `modal` classes and associated
 * jQuery plugin.
 *
 *     <ngeo-modal ng-model="modalShown">
 *       <div class="modal-header">
 *         <button type="button" class="close" data-dismiss="modal"
 *                 aria-hidden="true">&times;</button>
 *         <h4 class="modal-title">The Title</h4>
 *       </div>
 *       <div class="modal-body">Some content</div>
 *     </ngeo-modal>
 *
 * Note: for z-indexing purpose, the modal DOM element is automatically moved
 * to document body element.
 *
 * See our live example: {@link ../examples/modal.html}
 *
 * @param {angular.$parse} $parse Angular parse service.
 * @return {angular.Directive} The directive specs.
 * @htmlAttribute {boolean} ngeo-modal-destroy-content-on-hide Destroy the content
 * when the modal is hidden
 * @ngInject
 * @ngdoc directive
 * @ngname ngeoModal
 */
ngeo.modalDirective = function($parse) {
  return {
    template: '<div class="modal fade" tabindex="-1" role="dialog">' +
        '<div class="modal-dialog">' +
        '<div class="modal-content">' +
        '</div>' +
        '</div>' +
        '</div>',
    restrict: 'E',
    require: 'ngModel',
    transclude: true,
    link:
        /**
         * @param {!angular.Scope} scope Scope.
         * @param {angular.JQLite} element Element.
         * @param {angular.Attributes} attrs Attributes.
         * @param {angular.NgModelController} ngModelController The ngModel
         * controller.
         */
        function(scope, element, attrs, ngModelController, transcludeFn) {
          var modal = element.children();
          var destroyContent = attrs['ngeoModalDestroyContentOnHide'] === 'true';
          var childScope = scope.$new();

          // move the modal to document body to ensure that it is on top of
          // other elements even if in a positioned element initially.
          angular.element(document.body).append(modal);

          ngModelController.$render = function() {
            modal.modal(ngModelController.$viewValue ? 'show' : 'hide');
          };

          modal.on('shown.bs.modal hidden.bs.modal', function(e) {
            var type = e.type;
            goog.asserts.assert(type == 'shown' || type == 'hidden');
            scope.$apply(function() {
              ngModelController.$setViewValue(type == 'shown');
            });
          });

          if (destroyContent) {
            modal.on('hide.bs.modal', onHide);
            modal.on('show.bs.modal', onShow);
          } else {
            modal.find('.modal-content').append(transcludeFn());
          }

          function onShow(e) {
            childScope = scope.$new();
            transcludeFn(childScope, function(clone) {
              modal.find('.modal-content').append(clone);
            });
          }

          function onHide(e) {
            childScope.$destroy();
            modal.find('.modal-content').empty();
          }
        }
  };
};

ngeo.module.directive('ngeoModal', ngeo.modalDirective);

goog.provide('ngeo.popoverDirective');
goog.provide('ngeo.popoverAnchorDirective');
goog.provide('ngeo.popoverContentDirective');
goog.provide('ngeo.PopoverController');

goog.require('ngeo');

/**
 * Provides a directive used to display a Bootstrap popover.
 *
 *<div ngeo-popover>
 *  <a ngeo-popover-anchor class="btn btn-info">anchor 1</a>
 *  <div ngeo-popover-content>
 *    <ul>
 *      <li>action 1:
 *        <input type="range"/>
 *      </li>
 *    </ul>
 *  </div>
 *</div>
 * @ngdoc directive
 * @ngInject
 * @ngname ngeoPopover
 * @return {angular.Directive} The Directive Definition Object.
 */
ngeo.popoverDirective = function() {
  return {
    restrict: 'A',
    scope: true,
    controller: 'NgeoPopoverController',
    controllerAs : 'popoverCtrl',
    link: function(scope, elem, attrs, ngeoPopoverCtrl) {
      ngeoPopoverCtrl.anchorElm.on('hidden.bs.popover', function() {
        /**
         * @type {{inState : Object}}
         */
        var popover = ngeoPopoverCtrl.anchorElm.data('bs.popover');
        popover['inState'].click = false;
      });

      ngeoPopoverCtrl.anchorElm.on('inserted.bs.popover', function() {
        ngeoPopoverCtrl.bodyElm.show();
        ngeoPopoverCtrl.shown = true;
      });

      ngeoPopoverCtrl.anchorElm.popover({
        container: 'body',
        html: true,
        content: ngeoPopoverCtrl.bodyElm,
        placement : attrs['ngeoPopoverPlacement'] || 'right'
      });

      if (attrs['ngeoPopoverDismiss']) {
        $(attrs['ngeoPopoverDismiss']).on('scroll', function() {
          ngeoPopoverCtrl.dismissPopover();
        });
      }

      scope.$on('$destroy', function() {
        ngeoPopoverCtrl.anchorElm.popover('destroy');
        ngeoPopoverCtrl.anchorElm.unbind('inserted.bs.popover');
        ngeoPopoverCtrl.anchorElm.unbind('hidden.bs.popover');
      });
    }
  };
};

/**
 * @ngdoc directive
 * @ngInject
 * @ngname ngeoPopoverAnchor
 * @return {angular.Directive} The Directive Definition Object
 */
ngeo.popoverAnchorDirective = function() {
  return {
    restrict: 'A',
    require: '^^ngeoPopover',
    link: function(scope, elem, attrs, ngeoPopoverCtrl) {
      ngeoPopoverCtrl.anchorElm = elem;
    }
  };
};

/**
 * @ngdoc directive
 * @ngInject
 * @ngname ngeoPopoverContent
 * @return {angular.Directive} The Directive Definition Object
 */
ngeo.popoverContentDirective = function() {
  return {
    restrict: 'A',
    require: '^^ngeoPopover',
    link : function(scope, elem, attrs, ngeoPopoverCtrl) {
      ngeoPopoverCtrl.bodyElm = elem;
      elem.hide();
    }
  };
};

/**
 * The controller for the 'popover' directive.
 * @constructor
 * @struct
 * @ngInject
 * @export
 * @ngdoc controller
 * @ngname NgeoPopoverController
 * @param {angular.Scope} $scope Scope.
 */
ngeo.PopoverController = function($scope) {
  /**
   * The state of the popover (displayed or not)
   * @type {boolean}
   * @export
   */
  this.shown = false;

  /**
   * @type {angular.JQLite|undefined}
   * @export
   */
  this.anchorElm = undefined;

  /**
   * @type {angular.JQLite|undefined}
   * @export
   */
  this.bodyElm = undefined;

  function onMouseDown(clickEvent) {
    if (this.anchorElm[0] !== clickEvent.target &&
      this.bodyElm.parent()[0] !== clickEvent.target &
      this.bodyElm.parent().find(clickEvent.target).length === 0 && this.shown) {
      this.dismissPopover();
    }
  }

  angular.element('body').on('mousedown', onMouseDown.bind(this));

  $scope.$on('$destroy', function() {
    angular.element('body').off('mousedown', onMouseDown);
  });
};


/**
 * Dissmiss popover function
 * @export
 */
ngeo.PopoverController.prototype.dismissPopover = function() {
  this.shown = false;
  this.anchorElm.popover('hide');
};

ngeo.module.controller('NgeoPopoverController', ngeo.PopoverController);
ngeo.module.directive('ngeoPopover', ngeo.popoverDirective);
ngeo.module.directive('ngeoPopoverAnchor', ngeo.popoverAnchorDirective);
ngeo.module.directive('ngeoPopoverContent', ngeo.popoverContentDirective);

